import styled from '@emotion/styled';

export const Container = styled.section`
  width: 100%;
  height: auto;
  background-color: white;
  text-align: center;
  padding-top: 118px;
  padding-bottom: 118px;
  margin: 0 auto;
`;
export const Title = styled.h2``;
