import styled from '@emotion/styled';

export const Container = styled.section`
  width: 100%;
  height: auto;
  background-color: white;
  text-align: center;
  padding-top: 118px;
  padding-bottom: 118px;
  margin: 0 auto;
`;
export const Title = styled.h2`
  font-size: 14px;
  font-weight: 700;
  line-height: 1.1;
  text-transform: uppercase;
  color: #212121;
  text-align: center;
  margin-top: 0;
  margin-bottom: 10px;
`;
export const List = styled.ul`
  display: inline-flex;
  flex-wrap: wrap;
  padding: 0px;
  list-style: none;
`;
export const Item = styled.li`
  padding: 10px;
`;
