import PropTypes from 'prop-types';
import { Container, Title } from './Statistics.styled.js';

function Statistics(props) {
  const { elements, title } = props;

  return (
    <Container class="statistics">
      {title && <Title class="title">{title}</Title>}

      <ul class="stat-list">
        {elements.map(el => (
          <li class="item" key={elements.id}>
            <span class="label">{elements.label}</span>
            <span class="percentage">{elements.percentage}</span>
          </li>
        ))}
      </ul>
    </Container>
  );
}

Statistics.propTypes = {
  statistics: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.string.isRequired,
      label: PropTypes.string.isRequired,
      percentage: PropTypes.number.isRequired,
    }),
  ).isRequired,
  title: PropTypes.string.isRequired,
};
export default Statistics;
