import styled from '@emotion/styled';
// import { Container } from '@testing-library/react';

export const Container = styled.div`
  display: flex;
  //   flex-direction: column;
  //   justify-content: center;
  //   align-items: center;
  background: coral;
  padding: 50px;
`;
export const Wrap = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;
export const Avatar = styled.img`
  position: contain;
  top: 4px;
  right: 4px;
  padding: 4px 8px;
  ${props => console.log(props.avatar)};
`;
export const Info = styled.p`
  display: flex;
  align-items: center;
  margin-top: 0px;
  margin-bottom: 8px;
  color: #gray;
  line-height: 22px;
  font-weight: 400;
  letter-spacing: 0.25px;
`;
